        public static string GetProviderAddress(BlockchainNetwork blockchainNetwork)
        {
            switch (blockchainNetwork)
            {
                default:
                case BlockchainNetwork.IgcTestnet:
                    return RPC_URL;
            }
        }

        public static string GetTokenContractABI(VirtualCurrencyID tokenID)
        {
            string contractAddress = string.Empty;

            switch (tokenID)
            {
                case VirtualCurrencyID.IG:
                    contractAddress = IGT_CONTRACT_ABI;
                    break;
                case VirtualCurrencyID.CO:
                    contractAddress = IGC_CONTRACT_ABI;
                    break;
            }

            return contractAddress;
        }

        public static string GetTokenContractAddress(VirtualCurrencyID tokenID)
        {
            string contractAddress = string.Empty;

            switch (tokenID)
            {
                case VirtualCurrencyID.IG:
                    contractAddress = IGT_CONTRACT_ADDRESS;
                    break;
                case VirtualCurrencyID.CO:
                    contractAddress = IGC_CONTRACT_ADDRESS;
                    break;
            }

            return contractAddress;
        }
    }
